            Char[] unicodeArray = unicodeChars.ToCharArray();
            Random random = new Random();
            for (int i = 0; i < 20; i++)
            {
                int randomNum = random.Next(0, unicodeArray.Length - 1);
                string character = unicodeArray[randomNum].ToString();
                salt += character;
            }
            return salt;
        }

        public static string HashPassword(string salt, string password)
        { 
            string concat = password + salt;
            return HashAlgorithmSha256(concat);
        }
        
        
        
        private static string HashAlgorithmSha256(string hashPassword)
            {
                // Create a SHA256   
                using SHA256 sha256Hash = SHA256.Create();
                // ComputeHash - returns byte array  
                byte[] bytes = sha256Hash.ComputeHash(Encoding.UTF8.GetBytes(hashPassword));  
  
                // Convert byte array to a string   
                StringBuilder builder = new StringBuilder();  
                foreach (var t in bytes)
                {
                    builder.Append(t.ToString("x2"));
                }  
                return builder.ToString();
            }  
                 
        }  
  
